package com.example.monitoringbatuk.ui.historyAudioimport android.content.Intentimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.monitoringbatuk.databinding.ItemHistoryAudioBindingimport com.example.monitoringbatuk.ui.playAudio.PlayAudioActivityclass HistoryAudioAdapter : RecyclerView.Adapter<HistoryAudioAdapter.AudioViewHolder>() {    private val listData = ArrayList<AudioHistory>()    init {        Log.d("data", listData.toString())    }    fun addAllData(data: ArrayList<AudioHistory>) {        listData.addAll(data)        notifyDataSetChanged()    }    inner class AudioViewHolder(private val binding: ItemHistoryAudioBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(audioHistory: AudioHistory) {            binding.apply {                tvFilename.text = audioHistory.filename                tvName.text = audioHistory.name                tvDateAndTime.text = audioHistory.date                tvFilename.isSelected  = true            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AudioViewHolder {        return AudioViewHolder(ItemHistoryAudioBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: AudioViewHolder, position: Int) {        holder.bind(listData[position])        holder.itemView.setOnClickListener {            val intent = Intent(holder.itemView.context, PlayAudioActivity::class.java)            intent.putExtra("URL_AUDIO", listData[position].url)            holder.itemView.context.startActivity(intent)        }    }    override fun getItemCount(): Int {        return listData.size    }}